import {
  LOAD_REQUEST,
  LOAD_SUCCESS,
  LOAD_FAILURE,
  INCREMENT_BID,
} from "./actionTypes";

const INITIAL_STATE = {
  data: [],
  loading: false,
  error: false,
};

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case LOAD_REQUEST:
      return { ...state, loading: true };

    case LOAD_SUCCESS:
      return {
        ...state,
        data: action.payload,
        loading: false,
        error: false,
      };

    case LOAD_FAILURE:
      return { ...state, loading: false, error: true, data: [] };

    case INCREMENT_BID:
      console.log("BID", action.payload);

      return {
        ...state,
        // data:
      };

    default:
      return state;
  }
};

export default reducer;
