import { useState } from "react";

export default function Count() {
  //   const end = new Date(2021, 11, 31).getTime();

  const [days, setDays] = useState();
  const [hours, setHours] = useState();
  const [minutes, setMinutes] = useState();
  const [seconds, setSeconds] = useState();

  setInterval(() => {
    const now = new Date().getTime();
    // const diff = end - now;

    setDays(Math.floor(now / (1000 * 60 * 60 * 24)));
    setHours(Math.floor((now % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)));
    setMinutes(Math.floor((now % (1000 * 60 * 60)) / (1000 * 60)));
    setSeconds(Math.floor((now % (1000 * 60)) / 1000));
  }, 1000);

  return (
    <div>
      <div class="flex">
        <div>
          <div>{days}</div>
          <div> days</div>
        </div>
        <span>:</span>
        <div>
          <div>{hours}</div>
          <div> hours</div>
        </div>
        <span>:</span>
        <div>
          <div>{minutes}</div>
          <div> minutes</div>
        </div>
        <span>:</span>
        <div>
          <div>{seconds}</div>
          <div> seconds</div>
        </div>
      </div>
    </div>
  );
}
