import {
  LOAD_REQUEST,
  LOAD_SUCCESS,
  LOAD_FAILURE,
  INCREMENT_BID,
} from "./actionTypes";

const INITIAL_STATE = {
  data: [],
  loading: false,
  error: false,
};
console.log("state", state);
const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case LOAD_REQUEST:
      return { ...state, loading: true };

    case LOAD_SUCCESS:
      return {
        ...state,
        data: action.payload,
        loading: false,
        error: false,
      };

    case LOAD_FAILURE:
      return { ...state, loading: false, error: true, data: [] };

    case INCREMENT_BID:
      console.log("BID", action.payload);
      const test = state.data.find((item) => item.id === action.payload);
      const lastItem = test.bids[test.bids.length - 1];
      console.log("BID", lastItem.amount);
      // console.log("BID", test.bids);

      const newBid = {
        amount: lastItem.amount + 250,
        dealership: "Instacarro",
        createdAt: "2018",
        channel: "Web",
      };
      const newTest = test.bids.push(newBid);
      console.log("BID", test);
      return {
        ...state,
        data: { ...state.data, ...newTest },
      };

    default:
      return state;
  }
};

export default reducer;
