import { LOAD_REQUEST, LOAD_SUCCESS, LOAD_FAILURE } from "./actionTypes";

const INITIAL_STATE = {
  data: [],
  loading: false,
  error: false,
};
console.log("state", INITIAL_STATE);

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case LOAD_REQUEST:
      return { ...state, loading: true };

    case LOAD_SUCCESS:
      console.log("load", action.payload.data);
      return {
        ...state,
        data: action.payload,
        loading: false,
        error: false,
      };

    case LOAD_FAILURE:
      return { ...state, loading: false, error: true, data: [] };

    case INCREMENT_DEBT:
      console.log("DEBTS", action.payload);
      const newListDebt = state.debts.filter(
        (item) => item.id !== action.payload
      );
      return {
        ...state,
        score: state.score + 15,
        debts: [...newListDebt],
      };

    default:
      return state;
  }
};

export default reducer;
