import React from "react";
import "./Card.css";

const Card = () => {
  return (
    <div className="container">
      <div className="card">
        <img src={"https://placeimg.com/300/200/people"} alt="people" />

        <h1 className="name">{this.props.name}</h1>

        <p>
          <span className="hometown">{this.props.hometown}</span>
          <span className="jobtitle">{this.props.job}</span>
        </p>

        <p className="bio">{this.props.bio}</p>

        <button onClick={this.addClass}>Get in touch</button>

        <div id="contact" className={this.state.class}>
          <i class="fas fa-phone-square"></i>
          <i class="fas fa-envelope-square"></i>
          <i class="fab fa-facebook-square"></i>
          <i class="fab fa-twitter-square"></i>
          <i class="fab fa-linkedin"></i>
        </div>
      </div>
    </div>
  );
};

export default Card;
